CREATE TABLE Mesoregiao (
	ID_Mesoregiao int IDENTITY (1, 1) NOT NULL,
	Descricao_Mesoregiao varchar (max) NOT NULL,
	Sigla_UF char (2) NOT NULL,
	Situacao BIT NULL DEFAULT 1,
	DataInclusao DATETIME NULL DEFAULT GETDATE(),
	DataAlteracao DATETIME NULL,
	DataExclusao DATETIME NULL,
	CONSTRAINT PK_Mesoregiao PRIMARY KEY (ID_Mesoregiao),
	CONSTRAINT FK_UF FOREIGN KEY (Sigla_UF) REFERENCES Unidades_Federacao(Sigla_UF)
)
